namespace App\Tests\Functionnal;

use App\Controller\MovieController;
use Symfony\Bundle\FrameworkBundle\Test\WebTestCase;
use Symfony\Component\HttpFoundation\Response;
use JMS\Serializer\SerializerInterface;

class MovieControllerTest extends WebTestCase
{
    /**
     * @var SerializerInterface
     */
    private $serializer;

    public function setUp(): void
    {
        $kernel = self::bootKernel();

        $this->serializer = $kernel->getContainer()->get('jms_serializer');
    }

    public function testMustObtainFourMovie()
    {
        $client = static::createClient();

        $client->request('GET', '/api/movie',
            [],
            [],
            [
                'HTTP_ACCEPT' => 'application/json'
            ]);

        $movies = $this->serializer->deserialize($client->getResponse()->getContent(), 'array<App\Entity\Movie>', 'json');

        $this->assertCount(4, $movies);
        $this->assertEquals(Response::HTTP_OK, $client->getResponse()->getStatusCode());
    }

